(1347, 'wpZXcMGf', 'Income profit and lose', '', 1345, 1, 42, '2016-10-18 07:25:11', 42, '2016-10-18 07:25:11', '', 'Income profit and lose', 0),
(1348, 'wpZXcMGf', 'Cashflow', '', 1345, 1, 42, '2016-10-18 07:25:11', 42, '2016-10-18 07:25:11', '', 'Cashflow', 0),
