(1446, 'FoNajTkJ', 'Cashflow', '', 1443, 1, 42, '2016-10-18 10:08:50', 42, '2016-10-18 10:08:50', '', 'Cashflow', 0),
(1447, 'FoNajTkJ', 'Balance Sheet', '', 1443, 1, 42, '2016-10-18 10:08:50', 42, '2016-10-18 10:08:50', '', 'Balance Sheet', 0),
